import React, { useState } from "react";
import { Link } from "react-router-dom";
import Navbar from "../components/Navbar";

export default function RegisterPage() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [office, setOffice] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Aquí iría la lógica para llamar al backend
    console.log("Registro:", { name, email, password, office });
    setMessage("Simulación de registro completado.");
  };

  return (
    <div className="min-h-screen flex flex-col">
      <Navbar user={null} />
      <div className="bg-white dark:bg-gray-800 flex flex-1 justify-center items-center">
        <form
          onSubmit={handleSubmit}
          className="bg-white dark:bg-gray-700 p-6 rounded shadow-md w-full max-w-sm flex flex-col gap-4"
        >
          <h1 className="text-3xl font-bold text-center text-gray-900 dark:text-gray-100 mb-4">
            Registro
          </h1>
          <input
            type="text"
            placeholder="Nombre"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="p-2 rounded border"
            required
          />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="p-2 rounded border"
            required
          />
          <input
            type="password"
            placeholder="Contraseña"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="p-2 rounded border"
            required
          />
          <input
            type="text"
            placeholder="Oficina"
            value={office}
            onChange={(e) => setOffice(e.target.value)}
            className="p-2 rounded border"
            required
          />
          <button
            type="submit"
            className="bg-blue-600 text-white p-2 rounded hover:bg-blue-700"
          >
            Registrarse
          </button>
          <p className="text-center text-sm text-gray-600 dark:text-gray-300 mt-2">
            ¿Ya tienes cuenta?{" "}
            <Link to="/login" className="text-blue-600 hover:underline">
              Login
            </Link>
          </p>
          {message && (
            <p className="text-center text-red-500 mt-2">{message}</p>
          )}
        </form>
      </div>
    </div>
  );
}
