import React from 'react';
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import { useSelector } from 'react-redux';
import LoginPage from "./pages/LoginPage";
import RegisterPage from "./pages/RegisterPage";
import ReportsList from "./pages/ReportsList";
import UploadReport from "./pages/UploadReport";
import Profile from "./pages/Profile";
import ProtectedAppLayout from "./components/ProtectedAppLayout";
import { useEffect } from "react";


function App() {
  const isLoggedIn = Boolean(localStorage.getItem("token"));
  const darkMode = useSelector(state => state.darkMode.darkMode);

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
  }, [darkMode]);

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/login" element={<LoginPage />} />
        <Route path="/register" element={<RegisterPage />} />
        <Route
          path="/reports"
          element={isLoggedIn ? <ProtectedAppLayout page="ReportsList" /> : <Navigate to="/login" replace />}
        />
        <Route
          path="/upload"
          element={isLoggedIn ? <ProtectedAppLayout page="UploadReport" /> : <Navigate to="/login" replace />}
        />
        <Route
          path="/profile"
          element={isLoggedIn ? <ProtectedAppLayout page="Profile" /> : <Navigate to="/login" replace />}
        />
        <Route path="*" element={<Navigate to={isLoggedIn ? "/reports" : "/login"} />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
